    event.preventDefault() 
    console.log('add burger',this.nameRef.current.value);
    const burger= {
      name: this.nameRef.current.value,
      name: this.nameRef.current.value,
    }

  };
  render() {
    return (
     <form className='burger-edit' onSubmit={this.createBurger}>
       <input 
       ref={this.nameRef}
       name='name' 
       type='text' 
       placeholder='Name' 
       autoComplete='off'/>
       <input
       ref={this.priceRef}
       name='price'
       type='text'
       placeholder='Price'
       autoComplete='off'
       />
       <select ref={this.statusRef} name='status' className='status'>
         <option value='available'>We have it </option>
         <option value='unavlable'>Take it off menu</option>
       </select>
       <textarea ref={this.descRef} name='desc' placeholder='Desc'/>
       <input 
       ref={this.imageRef}
       name='image' 
       type='text' 
       placeholder='Image' 
       autoComplete='off'
       />
       <button type='submit'>+ Add to the menu</button>
     </form>
    );
  }
}
export default AddBurgerForm;
